designsystem button.description:
{
 align-items: center;
background: var(--color--white);
border: 1px solid var(--color--bluedark);
border-radius: 36px;
box-sizing: border-box;
color: var(--color--bluedark);
cursor: pointer;
display: flex;
font-size: 14px;
line-height: calc(36px - 2px);
padding: 0 20px;
text-align: center;
text-decoration: none;
white-space: nowrap;
height: 36px;
 }
